import CustomStore from "devextreme/data/custom_store";

interface Options {
    key?: string|Array<string>,

    loadUrl?: string,
    loadParams?: Object,
    loadMethod?: string,

    updateUrl?: string,
    updateMethod?: string,

    insertUrl?: string,
    insertMethod?: string,

    deleteUrl?: string,
    deleteMethod?: string,

    onBeforeSend?: (operation: string, ajaxSettings: {
        cache?: boolean;
        contentType?: any;
        data?: any;
        dataType?: string;
        headers?: { [key: string]: any; };
        method?: string;
        password?: string;
        timeout?: number;
        url?: string;
        username?: string;
        xhrFields?: { [key: string]: any; };
    }) => void
}

export function createStore(options: Options): CustomStore;
